import jsonPlaceholder from '../apis/jsonPlaceholder';

// action creator, this makes the api request
export const fetchPosts = async () => {
    const response = await jsonPlaceholder.get('/posts');

    return {
        type: 'FETCH_POSTS',
        payload: response
    };
};
