import React from 'react';

// *** moved from app.js to here to accomodate context system over redux
// this.props.onLanguageChange because this is where we are getting our function from
class LanguageSelector extends React.Component {
    render(){
        return (
            <div>
                Select a language:
                <i className="flag us" onClick={() => this.props.onLanguageChange('english')}/>
                <i className="flag nl" onClick={() => this.props.onLanguageChange('dutch')}/>
            </div>
        );
    }
}

export default LanguageSelector;
